import { useState } from "react";
import { ethers } from "ethers";
import * as tokenAbi from '../abi/token.json'

function App() {
  let [text, setText] = useState("");
  let [savedText, setSavedText] = useState("");
  let [connected, setConnected] = useState(false);

  let { ethereum } = window;
  let tokenContract = null;

  if (ethereum) {
    let address = "0xB994b0F2579587f5E6b2ea5972D3398f04c00628";
    let provider = new ethers.providers.Web3Provider(ethereum);
    let signer = provider.getSigner();
    contract = new ethers.Contract(address, tokenAbi, signer);
  }

  return (
    <div className="App">

      <button onClick={() => {
          if (contract && !connected) {
              ethereum.request({ method: 'eth_requestAccounts'})
                  .then(accounts => {
                      setConnected(true);
                  })
          }
      }}>{!connected ? 'Connect wallet' : 'Connected' }</button>

      <form onSubmit={(e) => {
        e.preventDefault();
        if (contract && connected) {
          contract.changeText(text)
            .then(() => {
              setText("");
            });
        }
      }}>
          <input type="text" placeholder="Enter text" onChange={e => setText(e.currentTarget.value)} value={text} />
          <input type="submit" value="save to contract" />
      </form>

      <button onClick={() => {
        if (contract && connected) {
          contract.text()
            .then(text => {
              setSavedText(text);
            })
        }
      }}>Get Text</button>

      <span>{savedText}</span>
    </div>
  );
}

export default App;
